example.vc:
The method of judging "vacant" data.

#include "fwlib32.h"

void example( void )
{
    short       ret;
    unsigned long   s_no = 20000;
    unsigned long   num;
    unsigned short  type = 0;
    double      mcval;
    long        *rval;
    long        lval;
    long        hval;

    num = 1;
    ret = cnc_rdpmacror2( Hndl, s_no, &num, type, &mcval );
    rval = (long*)&mcval;
    lval = (long)(*rval++);
    hval = (long)(*rval);
    if (( lval == (long)0xffffffff ) && ( hval == (long)0xffffffff )) { 
        ; // "vacant"
    }

}

example.vcs:
The method of judging "vacant" data.

class example
{
    public void sample()
    {
        short ret;
        uint s_no = 20000;
        uint num;
        ushort type = 0;
        byte[] mcval = new byte[8];
        uint lval;
        uint hval;

        num = 1;
        ret = Focas1.cnc_rdpmacror2(h, s_no, ref num, type, mcval);
        lval = (uint)((mcval[3] << 24) | (mcval[2] << 16) | (mcval[1] << 8) | mcval[0]);
        hval = (uint)((mcval[7] << 24) | (mcval[6] << 16) | (mcval[5] << 8) | mcval[4]);
        if ((lval == 0xffffffff) && (hval == 0xffffffff))
        {
            ; // "vacant"
        }
    }
}
