content.pre:
typedef struct iodbpr {
    long  datano_s; /* start P code macro variable number*/
    short dummy;    /* (not used) */
    long  datano_e; /* end P code macro variable number */
    struct {
          long  mcr_val; /* value of P code macro */
          short dec_val; /* number of places of decimals */
    } data[N];           /* N:Number of P code macro variable */
} IODBPR;

example.vc:
The following program writes the specified values into the P code macro
variables within the specified range.

#include "fwlib32.h"

/* start is start variable number to be written. */
/* value is array of value to be written. */
/* number is number of variable. */
short example( long start, long *value, short number )
{
        IODBPR *pmacror ;
        short ret ;
        long idx ;
        pmacror = (IODBPR *)malloc( 12+8*number ) ;
        pmacror->datano_s = start ;
        pmacror->datano_e = start + number - 1 ;
        for ( idx = 0 ; idx < number ; idx++ ) {
                pmacror->data[idx].mcr_val = value[idx] ;
                pmacror->data[idx].dec_val = 0 ;
        }
        ret = cnc_wrpmacror( h, 12+8*number, pmacror ) ;
        free( pmacror ) ;
        return ( ret ) ;
}

example.vcs:
The following program writes the specified values into the P code macro
variables within the specified range.

class example
{
    /* start is start variable number to be written. */
    /* value is array of value to be written. */
    /* number is number of variable. */
    public short sample(int start, int[] value, short number)
    {
            Focas1.IODBPR      pmcrHead = new Focas1.IODBPR();
            Focas1.IODBPR_data pmcrData = new Focas1.IODBPR_data();
            short ret;
            int idx;

            byte[] bytes = new byte[12 + 8 * number];
            IntPtr ptrHead = Marshal.AllocCoTaskMem(Marshal.SizeOf(pmcrHead));
            IntPtr ptrData = Marshal.AllocCoTaskMem(Marshal.SizeOf(pmcrData));

            pmcrHead.datano_s = start;
            pmcrHead.datano_e = start + number - 1;
            Marshal.StructureToPtr(pmcrHead, ptrHead, false);
            Marshal.Copy(ptrHead, bytes, 0, 12);
            for (idx = 0; idx < number; idx++)
            {
                    pmcrData.mcr_val = value[idx];
                    pmcrData.dec_val = 0;

                    int pos = 12 + idx * Marshal.SizeOf(pmcrData);
                    Marshal.StructureToPtr(pmcrData, ptrData, false);
                    Marshal.Copy(ptrData, bytes, pos, Marshal.SizeOf(pmcrData));
            }
            ret = Focas1.cnc_wrpmacror(h, (ushort)(12 + 8 * number), bytes);
            Marshal.FreeCoTaskMem(ptrHead);
            Marshal.FreeCoTaskMem(ptrData);
            return (ret);
    }
}
