example.vc:
The following program executes the NC commands by DNC operation.

        M3 S1500 ;
        G28 U0 W0 ;
        T0101 ;
        G0 X35. Z-10. ;
        M30 ;

#include "fwlib32.h"

short example( void )
{
        char* prg =
                "\n"
                "M3S1500\n"
                "G28U0W0\n"
                "T0101\n"
                "G0X35.Z-10.\n"
                "M30\n"
                "%";

        char fname[16];
        long len, n;
        short ret;

        ret = cnc_dncstart2( h, fname ) ;
        if ( ret ) return ( ret ) ;

        len = strlen( prg ) ;
        while ( len > 0 ) {
                n = len ;
                ret = cnc_dnc2( h, &n, prg ) ;
                if ( ret == EW_BUFFER ) {
                        continue ;
                }
                if ( ret == EW_OK ) {
                        prg += n ;
                        len -= n ;
                }
                if ( ret != EW_OK ) {
                        break ;
                }
        }
        if ( ret == EW_RESET )
                ret = cnc_dncend2( h, DNC_CANCEL ) ;
        else
                ret = cnc_dncend2( h, DNC_NORMAL ) ;
        return ( ret ) ;
}

example.vcs:
The following program executes the NC commands by DNC operation.

        M3 S1500 ;
        G28 U0 W0 ;
        T0101 ;
        G0 X35. Z-10. ;
        M30 ;

class example
{
    public short sample()
    {
            string strPrg = 
                    "\n"            +
                    "M3S1500\n"     +
                    "G28U0W0\n"     +
                    "T0101\n"       +
                    "G0X35.Z-10.\n" +
                    "M30\n"         +
                    "%";

            char[] fname = new char[16];
            int len, n;
            short ret;

            ret = Focas1.cnc_dncstart2(h, fname);
            if (ret != Focas1.EW_OK) return (ret);

            int startPos = 0;
            len = strPrg.Length;
            while (len > 0)
            {
                    char[] prg = new char[1280];
                    strPrg.CopyTo(startPos, prg, 0, len);
                    n = len;
                    ret = Focas1.cnc_dnc2(h, ref n, prg);
                    if (ret == (short)Focas1.focas_ret.EW_BUFFER)
                    {
                            continue;
                    }
                    if (ret == Focas1.EW_OK)
                    {
                            startPos += n;
                            len -= n;
                    }
                    if (ret != Focas1.EW_OK)
                    {
                            break;
                    }
            }
            if (ret == (short)Focas1.focas_ret.EW_RESET)
            {
                ret = Focas1.cnc_dncend2(h, (short)Focas1.focas_ret.DNC_CANCEL);
            }
            else
            {
                ret = Focas1.cnc_dncend2(h, (short)Focas1.focas_ret.DNC_NORMAL);
            }
            return (ret);
    }
}
