example.vc:
The following program registers the following NC program to CNC.

        O1234 ;
        M3 S1200 ;
        G0 Z0 ;
        G0 X0 Y0 ;
        G1 F500 X120. Y-30. ;
        M30 ;

#include "fwlib32.h"

short example( void )
{
        char* prg =
                "\n"
                "O1234\n"
                "M3 S1200\n"
                "G0 Z0\n"
                "G0 X0 Y0\n"
                "G1 F500 X120. Y-30.\n"
                "M30\n"
                "%";

        long len, n;
        short ret;

        ret = cnc_dwnstart3( h, 0 ) ;
        if ( ret ) return ( ret ) ;

        len = strlen( prg ) ;
        while ( len > 0 ) {
                n = len ;
                ret = cnc_download3( h, &n, prg ) ;
                if ( ret == EW_BUFFER ) {
                        continue ;
                }
                if ( ret == EW_OK ) {
                        prg += n ;
                        len -= n ;
                }
                if ( ret != EW_OK ) {
                        break ;
                }
        }
        ret = cnc_dwnend3( h ) ;
        return ( ret ) ;
}

example.vcs:
The following program registers the following NC program to CNC.

        O1234 ;
        M3 S1200 ;
        G0 Z0 ;
        G0 X0 Y0 ;
        G1 F500 X120. Y-30. ;
        M30 ;

class example
{
    public short sample()
    {
            string strPrg =
                    "\n"                    +
                    "O1234\n"               +
                    "M3 S1200\n"            +
                    "G0 Z0\n"               +
                    "G0 X0 Y0\n"            +
                    "G1 F500 X120. Y-30.\n" +
                    "M30\n"                 +
                    "%";

            int len, n;
            short ret;

            ret = Focas1.cnc_dwnstart3(h, 0);
            if (ret != Focas1.EW_OK) return (ret);

            int startPos = 0;
            len = strPrg.Length;
            while (len > 0)
            {
                    char[] prg = new char[1280];
                    strPrg.CopyTo(startPos, prg, 0, len);

                    n = len;
                    ret = Focas1.cnc_download3(h, ref n, prg);
                    if (ret == (short)Focas1.focas_ret.EW_BUFFER)
                    {
                            continue;
                    }
                    if (ret == Focas1.EW_OK)
                    {
                            startPos += n;
                            len -= n;
                    }
                    if (ret != Focas1.EW_OK)
                    {
                            break;
                    }
            }
            ret = Focas1.cnc_dwnend3(h);
            return (ret);
    }
}
